import re

def strFilter(element):
    exceptList = ['show', 'stuff', 'dexter', 'netapp','tactic', '&',
                  'Cache','DATA','temp','shot','the','assetlib', '', 'scenes', 'pub']
    if element in exceptList:
        return False
    else:
        return True
        
def run(filepath):
    filepath = filepath.replace('(', '')
    filepath = filepath.replace(')', '')
    tags = filter(strFilter, re.split('\s|/|[.]|[,]|-|_|:', filepath))
    return list(set(tags))
